const tabs = document.querySelectorAll(".tab-titles");
const tabMenu = document.querySelectorAll(".tab-content");
const serviceCards = document.querySelectorAll(".service-card");

function photoChange(image){
    let att = image.getAttribute("src");
    document.querySelector(".main-image").setAttribute("src",att);    
}


function cardChange(card) {
    let index = 0;
    for(let i = 0; i < serviceCards.length; i++) {
        if(serviceCards[i].querySelector(".services").classList.contains("active")) {
            serviceCards[i].querySelector(".services").classList.remove("active");
        }
        if(card === serviceCards[i] && !serviceCards[i].querySelector(".services").classList.contains("active")){
            card.querySelector(".services").classList.add("active");
            index = i;
        }
    }
    for(let i = 0; i < serviceCards.length; i++) {
        if(serviceCards[i].querySelector(".service-title").classList.contains("active")) {
            serviceCards[i].querySelector(".service-title").classList.remove("active");
        }
    }
    if(!card.querySelector(".service-title").classList.contains("active")){
        card.querySelector(".service-title").classList.add("active")
    }
}


function tabsFunction(btn) {
    let index = 0;
    for(let i = 0; i < tabs.length; i++) {
        if(tabs[i] === btn && !tabs[i].classList.contains("active")){
            tabs[i].classList.add("active");
        }
        else {
            tabs[i].classList.remove("active");
        }
        if(tabs[i] === btn) {
            index = i;
        }
    }
    
    for(let i = 0; i < tabMenu.length; i++) {
        if(tabMenu[i].classList.contains("active")&& i != index){
            console.log("input")
            tabMenu[i].classList.remove("active");
            break;
        }
    }
    if(!tabMenu[index].classList.contains("active")){
        tabMenu[index].classList.add("active");
    }
    
}